package ex01변수;

public class Ex01변수개요 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 한줄주석
		// 컴퓨터가 실행하지 않고 넘어가는 부분
		// 보통 주석에는 코드의 설명을 작성한다
		/* 
		 * 여러줄 주석
		 * */
		//Hello World!를 콘솔창에 출력하기
		System.out.println("Hello World!");
		
		//System.out.println 단축키:syso 
		// 단축키 실행: ctrl + space
		System.out.println("안녕 세상아!"); 
		
		// 자바 실행 구조
		// .java -> .class -> JVM -> 프로그램 실행
		
		//변수(식별자)
		// -데이터를 저장하는 임시적인 공간
		
		// 정수형 변수 num에 10을 저장하기
		int num = 10;
		
		// 아래로 복사 단축키: ctrl + alt + 아래화살표
		System.out.println(num);
		System.out.println(num);
		System.out.println(num);
		System.out.println(num);
		System.out.println(num);
		
	}

}
